INSERT INTO `content` VALUES (81,1,'Pourquoi louer?',NULL,'<table><tbody>\n<tr>\n<td><span style=\"font-family: RobotoRegular;\"><img src=\"/media/tinymce/source/CMS/pourquoi/pouquoilouer-first.png\" alt=\"\" width=\"158\" height=\"191\"></span></td>\r\n<td>\n<span style=\"font-family: RobotoBold;\">Vous voyagez plus léger!</span><br><span style=\"font-family: RobotoRegular;\">Quel plaisir de profiter de son voyage sans être encombré de bagages.</span><br><br><span style=\"font-family: RobotoRegular;\">Vous aimez voyager, mais depuis l\'arrivée de bébé, un simple week-end prend une tournure de déménagement.</span><br><span style=\"font-family: RobotoRegular;\">Dans vos déplacements, pour éviter tous ces bagages encombrants vous pouvez maintenant voyager léger et louer poussette, lit, table à langer, siège-auto, etc .</span><br><span style=\"font-family: RobotoRegular;\">Kidlou livre tout le matériel nécessaire sur votre lieu de séjour.</span>\n</td>\r\n</tr>\n<tr>\n<td>\n<span style=\"font-family: RobotoBold;\">Gagnez en sécurité sans trop investir!</span><br><span style=\"font-family: RobotoRegular;\">Le matériel pour bébé coûte cher et est souvent peu utilisé.</span><br><br><span style=\"font-family: RobotoRegular;\">Avec Kidlou, les grands-parents qui reçoivent leurs petits-enfants profitent d\'équipements toujours adaptés à l\'âge l\'enfant : la sécurité avant tout.</span>\n</td>\r\n<td><span style=\"font-family: RobotoRegular;\"> <img src=\"/media/tinymce/source/CMS/pourquoi/pourquoilouer-second.jpg\" alt=\"\" width=\"244\" height=\"244\"></span></td>\r\n</tr>\n<tr>\n<td><span style=\"font-family: RobotoRegular;\"> <img src=\"/media/tinymce/source/CMS/pourquoi/pouquoilouer-third.jpg\" alt=\"\" width=\"193\" height=\"302\"></span></td>\r\n<td>\n<span style=\"font-family: RobotoBold;\">Pensez à l\'environnement!</span><br><span style=\"font-family: RobotoRegular;\">La location est un mode de consommation responsable.</span><br><br><span style=\"font-family: RobotoRegular;\">Pourquoi stocker à la cave du matériel qui ne servira plus et qui sera finalement jeté.</span><br><span style=\"font-family: RobotoRegular;\">La location est un mode de consommation plus écologique qui va à l’encontre de la surconsommation</span><br><span style=\"font-family: RobotoRegular;\">Le matériel de puériculture de Kidlou sert à plusieurs familles alors consommez malin en louant à petit prix du matériel de qualité!</span>\n</td>\r\n</tr>\n</tbody></table>','2017-03-17 08:23:47','2017-03-22 15:18:39',1,NULL,0,1),(82,2,'Title',NULL,'<p>asdas asdas</p>','2017-03-17 08:23:47','2017-03-17 08:23:47',1,NULL,0,1),(83,3,'Title',NULL,'<p>asdas asdas</p>','2017-03-17 08:23:47','2017-03-17 08:23:47',1,NULL,0,1),(84,4,'Title',NULL,'<p>asdas asdas</p>','2017-03-17 08:23:47','2017-03-17 08:23:47',1,NULL,0,1),(85,1,'Test2226',NULL,'<p>tsts200</p>','2017-03-17 09:02:44','2017-03-17 12:43:51',1,NULL,0,2),(86,2,'Test2',NULL,'<p>tsts2</p>','2017-03-17 09:02:44','2017-03-17 13:33:25',1,NULL,0,2),(87,3,'Test',NULL,'<p>tsts</p>','2017-03-17 09:02:44','2017-03-17 13:36:05',1,NULL,0,2),(88,4,'Test',NULL,'<p>tsts</p>','2017-03-17 09:02:44','2017-03-17 09:02:44',1,NULL,0,2),(89,1,'asd300',NULL,'<p>asd asdas300</p>','2017-03-17 09:03:06','2017-03-17 12:43:51',1,NULL,10,3),(90,2,'asd',NULL,'<p>asd asdas</p>','2017-03-17 09:03:06','2017-03-17 13:33:25',1,NULL,10,3),(91,3,'asd',NULL,'<p>asd asdas</p>','2017-03-17 09:03:06','2017-03-17 13:36:05',1,NULL,10,3),(92,4,'asd',NULL,'<p>asd asdas</p>','2017-03-17 09:03:06','2017-03-17 09:03:06',1,NULL,10,3),(93,1,'Hallo 2',NULL,'<p>Hallo 2</p>','2017-03-17 09:36:33','2017-03-17 09:36:33',0,NULL,0,4),(94,2,'Hallo 2',NULL,'<p>Hallo 2</p>','2017-03-17 09:36:33','2017-03-17 09:36:33',0,NULL,0,4),(95,3,'Hallo 2',NULL,'<p>Hallo 2</p>','2017-03-17 09:36:33','2017-03-17 09:36:33',0,NULL,0,4),(96,4,'Hallo 2',NULL,'<p>Hallo 2</p>','2017-03-17 09:36:33','2017-03-17 09:36:33',0,NULL,0,4),(97,1,'Test400',NULL,'<p>test400</p>','2017-03-17 12:37:46',